
create table #ReasonAdjustment( AdjustmentReasonCode varchar(5), Description nvarchar(4000), EffectiveDate datetime,  DeactivationDate datetime)








select c.AdjustmentReasonCode, LEFT(c.Description, 250), c.EffectiveDate, c.DeactivationDate, getdate()
from #ReasonAdjustment c
LEFT JOIN AdjustmentReason s ON s.AdjustmentReasonCode = c.AdjustmentReasonCode
WHERE s.AdjustmentReasonCode IS NULL


Update s
SET Description = LEFT( t.Description, 250 )
FROM  AdjustmentReason s
	INNER JOIN #ReasonAdjustment t ON t.AdjustmentReasonCode = s.AdjustmentReasonCode 
WHERE s.Description <> t.Description


drop table #ReasonAdjustment
