-- Add new property to control usage of the CLR
IF NOT EXISTS (SELECT * FROM CustomerProperties WHERE [Key] = 'ParseEraUsingSqlClr')
BEGIN
	INSERT INTO CustomerProperties ([Key], Value)
	VALUES ('ParseEraUsingSqlClr', 1)
END

-- Update CLR assembly 

DECLARE @content VARBINARY(MAX)
SET @content = 

IF NOT EXISTS (SELECT NAME FROM sys.assemblies WHERE name = 'Kareo.Database.Helper')
BEGIN
	-- CREATE IF NOT EXISTS
	CREATE ASSEMBLY [Kareo.Database.Helper]
	AUTHORIZATION [dbo]
	FROM @content
	WITH PERMISSION_SET = SAFE
END
ELSE IF EXISTS (SELECT TOP 1 NAME FROM sys.assembly_files WHERE name = 'Kareo.Database.Helper' AND content <> @content)
BEGIN
	-- ALTER IF DIFFERENT THAN EXISTING.  Otherwise is errors out with an 'identical' assmbley error.
	ALTER ASSEMBLY [Kareo.Database.Helper]
	FROM @content
	WITH PERMISSION_SET = SAFE
END
GO


-- Link new ParseEra CLR sproc
IF EXISTS (
	SELECT	*
	FROM	SYSOBJECTS
	WHERE	Name = 'PaymentStoredProcedures_ParseERA'
	AND	TYPE = 'PC'
)
BEGIN
	DROP PROCEDURE dbo.PaymentStoredProcedures_ParseERA
END
GO

CREATE PROCEDURE PaymentStoredProcedures_ParseERA (@eraXml XML, @adjudicationDate DATETIME OUTPUT)
WITH EXECUTE AS CALLER
AS
EXTERNAL NAME [Kareo.Database.Helper].[StoredProcedures].[ParseEra] 

