	SupportCaseTypeID int not null Identity(1, 1),
	Caption varchar(127) not null,
	Comment varchar(max),
	SupportPolicy varchar(max),
	TargetQueue varchar(18), -- salesforce target Queue to assign cases to
	RequiresSupportPlan bit not null default 0,
	AllowsSubmission bit not null default 1,
	SortOrder int not null default 1000,
	Visible bit not null default 1)
	

