CREATE TABLE ##hcpcs_codes_insert
    (
      [ProcedureCode] [varchar](16) NOT NULL ,
      [RangeStart] VARCHAR(16) NULL ,
      [RangeEnd] VARCHAR(16) NULL ,
      [TypeOfServiceCode] [char](1) NOT NULL ,
      [Active] [bit] NOT NULL ,
      [OfficialName] [varchar](300) NULL ,
      [LocalName] [varchar](100) NULL ,
      [OfficialDescription] [varchar](1200) NULL
    );
INSERT  INTO ##hcpcs_codes_insert
        ( [ProcedureCode] ,
          [TypeOfServiceCode] ,
          [Active] ,
          [OfficialName] ,
          [LocalName] ,
          [OfficialDescription]
        )
        SELECT  HC.HcpcsCode AS [ProcedureCode] ,
                1 AS [TypeOfServiceCode] ,
                1 AS [Active] ,
                SUBSTRING(COALESCE(HC.ShortDescription, HC.LongDescription), 1,
                          300) AS [OfficialName] ,
                NULL AS [LocalName] ,
                HC.LongDescription AS [OfficialDescription]
        FROM    ##hcpcs_codes AS HC
                LEFT OUTER JOIN dbo.ProcedureCodeDictionary AS PCD WITH ( NOLOCK ) ON PCD.ProcedureCode = HC.HcpcsCode
        WHERE   HC.Source IN ( 1 )
                AND PCD.ProcedureCode IS NULL
        UNION ALL
        SELECT  HC.HcpcsCode AS [ProcedureCode] ,
                1 AS [TypeOfServiceCode] ,
                1 AS [Active] ,
                SUBSTRING(COALESCE(HC.ShortDescription, HC.LongDescription), 1,
                          300) AS [OfficialName] ,
                NULL AS [LocalName] ,
                HC.LongDescription AS [OfficialDescription]
        FROM    ##hcpcs_codes AS HC
                LEFT OUTER JOIN dbo.ProcedureCodeDictionary AS PCD WITH ( NOLOCK ) ON PCD.ProcedureCode = HC.HcpcsCode
        WHERE   HC.Source IN ( 2, 3, 4 )
                AND HC.TerminationDate IS NULL
                AND PCD.ProcedureCode IS NULL
        UNION ALL
        SELECT  HC.HcpcsCode AS [ProcedureCode] ,
                1 AS [TypeOfServiceCode] ,
                1 AS [Active] ,
                SUBSTRING(COALESCE(HC.ShortDescription, HC.LongDescription), 1,
                          300) AS [OfficialName] ,
                NULL AS [LocalName] ,
                HC.LongDescription AS [OfficialDescription]
        FROM    ##hcpcs_codes AS HC
                LEFT OUTER JOIN dbo.ProcedureCodeDictionary AS PCD WITH ( NOLOCK ) ON PCD.ProcedureCode = HC.HcpcsCode
        WHERE   HC.Source IN ( 5, 8 )
                AND PCD.ProcedureCode IS NULL
                AND HC.Action = 'new'
        UNION ALL
        SELECT  HC.HcpcsCode AS [ProcedureCode] ,
                1 AS [TypeOfServiceCode] ,
                1 AS [Active] ,
                SUBSTRING(COALESCE(HC.ShortDescription, HC.LongDescription), 1,
                          300) AS [OfficialName] ,
                NULL AS [LocalName] ,
                HC.LongDescription AS [OfficialDescription]
        FROM    ##hcpcs_codes AS HC
                LEFT OUTER JOIN dbo.ProcedureCodeDictionary AS PCD WITH ( NOLOCK ) ON PCD.ProcedureCode = HC.HcpcsCode
        WHERE   HC.Source IN ( 6, 7 )
                AND PCD.ProcedureCode IS NULL
                AND HC.Action = 'ADD';

UPDATE  HCI
SET     [TypeOfServiceCode] = TC.Tos ,
        RangeStart = TC.RangeStart ,
        RangeEnd = TC.RangeEnd
FROM    ##hcpcs_codes_insert AS HCI
        INNER JOIN ##tos_codes AS TC ON HCI.ProcedureCode BETWEEN TC.RangeStart
                                                          AND TC.RangeEND
WHERE   TC.RangeStart IS NOT NULL
        AND ISNUMERIC(HCI.ProcedureCode) = ISNUMERIC(COALESCE(TC.RangeStart,
                                                              HCI.ProcedureCode))
        AND SUBSTRING(HCI.ProcedureCode, LEN(HCI.ProcedureCode), 1) = ( CASE
                                                              WHEN ISNUMERIC(HCI.ProcedureCode) = 1
                                                              THEN SUBSTRING(HCI.ProcedureCode,
                                                              LEN(HCI.ProcedureCode),
                                                              1)
                                                              ELSE SUBSTRING(COALESCE(TC.RangeStart,
                                                              HCI.ProcedureCode),
                                                              LEN(COALESCE(TC.RangeStart,
                                                              HCI.ProcedureCode)),
                                                              1)
                                                              END )
        AND SUBSTRING(HCI.ProcedureCode, 1, 1) = ( CASE WHEN ISNUMERIC(HCI.ProcedureCode) = 1
                                                        THEN SUBSTRING(HCI.ProcedureCode,
                                                              1, 1)
                                                        ELSE SUBSTRING(COALESCE(TC.RangeStart,
                                                              HCI.ProcedureCode),
                                                              1, 1)
                                                   END );

