INSERT  INTO ##hcpcs_codes_deactivate
        ( ProcedureCode
        )
        SELECT  HC.HcpcsCode AS [ProcedureCode]
        FROM    dbo.ProcedureCodeDictionary AS PCD WITH ( NOLOCK )
                LEFT OUTER JOIN ##hcpcs_codes AS HC ON HC.HcpcsCode = PCD.ProcedureCode
        WHERE   HC.Source IN ( 1 )
                AND HC.HcpcsCode IS NULL
                AND PCD.Active = 1
        UNION ALL
        SELECT  HC.HcpcsCode AS [ProcedureCode]
        FROM    dbo.ProcedureCodeDictionary AS PCD WITH ( NOLOCK )
                INNER JOIN ##hcpcs_codes AS HC ON HC.HcpcsCode = PCD.ProcedureCode
        WHERE   HC.Source IN ( 2, 3, 4 )
                AND HC.TerminationDate IS NOT NULL
                AND PCD.Active = 1
        UNION ALL
        SELECT  HC.HcpcsCode AS [ProcedureCode]
        FROM    dbo.ProcedureCodeDictionary AS PCD WITH ( NOLOCK )
                INNER JOIN ##hcpcs_codes AS HC ON HC.HcpcsCode = PCD.ProcedureCode
        WHERE   HC.Source IN ( 5, 8 )
                AND HC.Action <> 'new'
                AND PCD.Active = 1
        UNION ALL
        SELECT  HC.HcpcsCode AS [ProcedureCode]
        FROM    dbo.ProcedureCodeDictionary AS PCD WITH ( NOLOCK )
                INNER JOIN ##hcpcs_codes AS HC ON HC.HcpcsCode = PCD.ProcedureCode
        WHERE   HC.Source IN ( 6, 7 )
                AND HC.Action = 'Discontinue'
                AND PCD.Active = 1;

